CREATE TABLE `category_content` (
  `id` int(11) NOT NULL,
  `service_category_id` int(11) NOT NULL,
  `category_content_item` varchar(255) NOT NULL,
  `category_content` text DEFAULT NULL,
  `category_icon` varchar(100) NOT NULL,
