INSERT INTO `category_content` (`id`, `service_category_id`, `category_content_item`, `category_content`, `category_icon`, `catagory_content_image`, `created_at`, `updated_at`) VALUES
(2, 1, 'Website Development', '<p>An overall curation and development of your website.</p>', 'ti-pencil-alt', '1597228066.jpg', '2020-08-12 10:27:46', '2020-08-12 10:27:46'),
(3, 1, 'Web Applications development', '<p>They are made easy with us!</p>', 'ti-vector', '1597228195.jpg', '2020-08-12 10:29:55', '2020-08-12 10:29:55'),
(4, 1, 'E-Commerce Development', '<p>Forging an online platform for a seamless buyer experience.</p>', 'ti-layers-alt', '1597228318.jpg', '2020-08-12 10:31:58', '2020-08-12 10:31:58'),
(5, 1, 'API Integration', '<p>Hatching an interface between data, applications, and devices that makes their interdependency smooth.</p>', 'ti-pie-chart', '1597228441.jpg', '2020-08-12 10:34:01', '2020-08-12 10:34:01'),
(6, 1, 'Mobile Application', '<p>When everything is available at a click, what are you waiting for?</p>\r\n\r\n<ul>\r\n	<li>Android</li>\r\n	<li>IOS<br />\r\n	&nbsp;</li>\r\n</ul>', 'ti-layers-alt', '1597228584.jpg', '2020-08-12 10:36:24', '2020-08-12 10:36:24'),
(7, 1, 'CRM & ERP', '<p>Making managing your relationships better with your consumers and employees.</p>', 'ti-layers-alt', '1597228723.jpg', '2020-08-12 10:38:43', '2020-08-12 10:38:43'),
(8, 3, 'digital strategy & planning', '<p>Attaching the power of social media with your brand.</p>', 'ti-pencil-alt', '1597228837.jpg', '2020-08-12 10:40:37', '2020-08-12 11:30:05'),
(9, 3, 'SEO & SEM', '<p>Lead your website in the race of ranking on google.</p>', 'ti-vector', '1597228972.jpg', '2020-08-12 10:42:52', '2020-08-12 11:30:25'),
(10, 3, 'email marketing', '<p>A little push to your organic marketing to make it a success.</p>\r\n\r\n<ol>\r\n	<li>Social Media Campaign</li>\r\n	<li>PPC</li>\r\n</ol>', 'ti-layers-alt', '1597229059.jpg', '2020-08-12 10:44:19', '2020-08-12 11:30:49'),
(11, 4, 'Blog Content', '<p>Discussing various aspects of your brand in a creative, quirky and interactive manner.</p>\r\n\r\n<ul>\r\n	<li>PR article</li>\r\n	<li>Newsletter Writing</li>\r\n	<li>Article Writing</li>\r\n</ul>', 'ti-pencil-alt', '1597229628.jpg', '2020-08-12 10:53:48', '2020-08-12 10:53:48'),
(12, 4, 'SEO Content', '<p>Your king level content should top the charts, which is our motive.</p>', 'ti-vector', '1597229675.jpg', '2020-08-12 10:54:35', '2020-08-12 10:54:35'),
(13, 4, 'Copywriting', '<p>Making every word count!</p>', 'ti-layers-alt', '1597229728.jpg', '2020-08-12 10:55:28', '2020-08-12 10:55:28'),
(14, 4, 'Website Content', '<p>Keeping it brief and comprehensive, we strive to make your content the face of your brand.</p>', 'ti-pie-chart', '1597229777.jpg', '2020-08-12 10:56:17', '2020-08-12 10:56:17'),
(15, 4, 'Video Content', '<p>Ideating, scripting and executing brand-specific video content.</p>', 'ti-pie-chart', '1597229846.jpg', '2020-08-12 10:57:26', '2020-08-12 10:57:26'),
(16, 5, 'photography', '<p>Integrating functionality with the &lsquo;look and feel&rsquo; of your online presence.</p>', 'ti-pencil-alt', '1597229932.jpg', '2020-08-12 10:58:52', '2020-08-12 11:35:17'),
(17, 4, 'promotional writing', NULL, 'ti-pencil-alt', '1597231679.jpg', '2020-08-12 11:27:59', '2020-08-12 11:27:59'),
(18, 3, 'third-party app marketing', '<p>A little push to your organic marketing to make it a success.</p>\r\n\r\n<ol>\r\n	<li>Social Media Campaign</li>\r\n	<li>PPC</li>\r\n</ol>', 'ti-layers-alt', '1597229059.jpg', '2020-08-12 10:44:19', '2020-08-12 11:30:49'),
(19, 5, 'graphic designing', NULL, 'ti-pencil-alt', '1597232204.jpg', '2020-08-12 11:36:44', '2020-08-12 11:36:44'),
(20, 5, 'videography', NULL, 'ti-pencil-alt', '1597232271.jpg', '2020-08-12 11:37:51', '2020-08-12 11:37:51'),
(21, 5, 'animation', NULL, 'ti-vector', '1597232311.jpg', '2020-08-12 11:38:31', '2020-08-12 11:38:31'),
(22, 6, 'public relations strategy and planning', NULL, 'ti-layers-alt', '1597232401.jpg', '2020-08-12 11:40:01', '2020-08-12 11:40:01'),
